  //-------------------------------------
  //---------- Setters/Getters ----------
  //-------------------------------------

  function setSavingState(state, msg, delay, cssClass) {
    setState(1, state, msg, delay, cssClass);
  }
  function setLoadingState(state, msg, delay, cssClass) {
    setState(2, state, msg, delay, cssClass);
  }
  function setHierarchyState(msg, delay, cssClass) {
    setState(3, true, msg, delay, cssClass);
  }
  function setState(type, state, msg, delay, cssClass) {
    if(type === 1) { //saving
      saving = state;
      var message = 'Saving...';
      var htmlElement = $('#hierarchySavingState');
    }
    else if(type === 2) { //loading
      loading = state;
      var message = 'Loading...';
      var htmlElement = $('#hierarchyLoadingState');
    }
    else { //other operation
      var message = 'Processing...';
      var htmlElement = $('#hierarchyState');
    }
    if(msg) {message = msg;}
    if(cssClass) {
      htmlElement.attr('class', cssClass);
    }
    htmlElement.html('<strong>'+message+'</strong>').show();
    if(delay) {
      htmlElement.delay(delay).queue(function(n) {
        $(this).hide().removeAttr('class').html('');
        n();
      });
    }
  }

  function setSavedContent() {
    savedContent = getCurrentContent();
  }

  function setCurrentPage(page) {
    currentPage = page;
    currentPageEscaped = currentPage.replace('%5C', '%5C%5C').replace(/\\/g, '%5C%5C').replace(/\./g, '%5C.');
  }
  function getCurrentPage(escaped) {
    if(escaped) {return currentPageEscaped;}
    else {return currentPage;}
  }

  function setCurrentSpace(folder) {
    currentSpace = folder;
    currentSpaceEscaped = getSpacesEscaped(currentSpace);
  }
  function getCurrentSpace(escaped, url) {
    if(escaped) {return currentSpaceEscaped;}
    else if(url) {return getURLSpacesString(currentSpace)}
    else {return currentSpace;}
  }

  function getCurrentContent() {
    var currentContent = '';
    if(currentEditor === 'object') {
      var arrayVal = getObjectInputFields();
      arrayVal.each(function(elemt, index) {
        currentContent += $(index).val();
      });
    }
    else if(currentEditor === 'wiki') {
      currentContent = $(textEditor).val();
    }
    return currentContent;
  }

  function getDocumentReferenceData(dataType) {
    switch(dataType) {
      case 'wiki':
        return xm ? xm.wiki : $('meta[name="wiki"]').attr('content');
        break;
      case 'space':
        return xm ? xm.space : $('meta[name="space"]').attr('content');
        break;
      case 'page':
        return xm ? xm.page : $('meta[name="page"]').attr('content');
        break;
      case 'fullName':
        return xm ? xm.document : $('meta[name="document"]').attr('content');
        break;
      case 'form_token':
        return xm ? xm.form_token : $('meta[name="form_token"]').attr('content');
        break;
      case 'restURL':
        return xm ? xm.restURL : $('meta[name="restURL"]').attr('content');
        break;
      default:
        return xm ? (xm.documentReference ? xm.documentReference : xm.wiki+':'+xm.space+'.'+xm.page) : $('meta[name="wiki"]').attr('content')+':'+$('meta[name="space"]').attr('content')+'.'+$('meta[name="page"]').attr('content');
    }
  }
  /**
   * Override or create a 'xm' variable in the class editor in order to get the right values.
   * "xm" (xwiki-meta) represents an XWiki document and should contain data about the master page.
   */
  function setDocumentReferenceData() {
    if(XWiki.editor == 'class') {
      xm = {
        wiki: getDocumentReferenceData('wiki'),
        space: $('#hierarchyMasterDocSpace').val(),
        page: $('#hierarchyMasterDocName').val(),
        document: $('#hierarchyMasterDocSpace').val()+'.'+$('#hierarchyMasterDocName').val(),
        form_token: getDocumentReferenceData('form_token'),
        restURL: '/xwiki/rest/wikis/'+getDocumentReferenceData("wiki")+'/'+getRestSpacesString($('#hierarchyMasterDocSpace').val())+'/pages/'+$('#hierarchyMasterDocName').val(),
        documentReference: getDocumentReferenceData('wiki')+':'+$('#hierarchyMasterDocSpace').val()+'.'+$('#hierarchyMasterDocName').val()
      };
    }
  }

  /**
   * loadPage()
   * savePage(string space, string page, string newContent, string newTitle, boolean changeSavedContent, boolean continueEditing)
   * cancelAndViewIDE()
   */
  function loadPage(file, folder) {
    if(!loading && currentEditor === 'wiki') {
      setLoadingState(true);
      setCurrentPage(file);
      setCurrentSpace(folder);
      var urlSpaces = getCurrentSpace(false, true);
      var urlPage = getCurrentPage(false);
      $.ajax({
        url: wikiViewURL+urlSpaces+"/"+urlPage+"?xpage=plain&raw=1&language=default",
        type: "GET",
        contentType: "plain/text",
      }).success(function(dataContent){
        $(textEditor).val($('<div />').html(dataContent.replace(/^<pre>/, '').replace(/<\/pre>$/, '')).text());
        if(syntaxHighlighting) {
          codeMirrors[0].getDoc().setValue(getCurrentContent());
        }
        setSavedContent();
        reloadHierarchy();
        setLoadingState(false, 'Loaded', 2500);
      });
    }
  }

  function savePage(space, page, newContent, newTitle, changeSavedContent, continueEditing) {
    if(!saving) {
      setSavingState(true);
      var restSpaces = getRestSpacesString(space);
      $.ajax({
        url: "/xwiki/rest/wikis/"+getDocumentReferenceData('wiki')+"/"+restSpaces+"/pages/"+page,
        type: "PUT",
        contentType: "application/x-www-form-urlencoded",
        accept: "application/xml",
        data: "title="+encodeURIComponent(newTitle)+"&content="+encodeURIComponent(newContent),
      }).error(function(xhr, errorStatus){
        setSavingState(false, xhr.statusText, 4000, 'red');
        return false;
      }).success(function(data){
        if(changeSavedContent) {
          setSavedContent();
        }
        $('#hierarchyStar').hide();
        setSavingState(false, 'Saved', 2500);
        if(continueEditing) return true;
        else window.location.href = wikiViewURL+currentSpace+'/'+currentPage;
      });
    }
  }

  function cancelAndViewIDE() {
    if(currentEditor == 'wiki' && savedContent != getCurrentContent()) {
      if(confirm("Are you sure?")) {
        window.location.href = wikiViewURL+currentSpace+'/'+currentPage;
      }
    }
    else {
      window.location.href = wikiViewURL+currentSpace+'/'+currentPage;
    }
  }

  //-------------------------------------
  //--------- Object Editor API ---------
  //-------------------------------------
  /**
   * loadObject()
   * startDisplayObjects(dataContent, urlSpaces, file)
   * loadObjectsDetails(objectsList, index, objectsListSize, oldClass, menuNode, dataNode)
   * endDisplayObjects(objectsListSize)
   * addObject(String className)
   * initObjects()
   * removeObject(String className, Int number)
   * saveObjects(boolean continueEdit)
   * synchronizeObjects()
   */

  function loadObject(file, folder) {
    if(!loading && currentEditor === 'object') {
      setLoadingState(true);
      setCurrentPage(file);
      setCurrentSpace(folder);
      var restSpaces = getRestSpacesString(folder);
      var urlSpaces = getURLSpacesString(folder);
      $.ajax({
        url: '/xwiki/rest/wikis/'+getDocumentReferenceData('wiki')+'/'+restSpaces+'/pages/'+file+'/objects',
        type: "GET",
        contentType: "application/xml",
      }).success(function(dataContent){
        startDisplayObjects(dataContent, urlSpaces, file);
      });
    }
  }

  /**
   * Start the loading of the object editor. Create the main DOM elements and call the function to continue the loading process.
   * @input XML dataContent : data from XWiki Rest API
   * @input String urlSpaces : the folder of the page in URL format (A/B for nested spaces)
   * @input String file : the selected page
   */
  function startDisplayObjects(dataContent, urlSpaces, file) {
    var form_token = xm ? xm.form_token : $('meta[name="form_token"]').attr('content');
    $(mainBlock).html('');
    var menuNode = $('<div id="editorObjectMenuContent"></div>').appendTo($('<div id="editorObjectMenu"><span id="editorObjectMenuTitle">Quick Access</span></div>').appendTo($(mainBlock)));
    var addObjectNode = $('<div id="addObjectBlock"></div>').appendTo($(mainBlock));
    var dataNode = $('<form method="post" action="/xwiki/bin/save/'+urlSpaces+'/'+file+'" id="objectIDEform"><input type="hidden" name="form_token" value="'+form_token+'" /></form>').appendTo($(mainBlock));
    var objectsList = $(dataContent).find('objectSummary');
    objectsList.sort(function(a, b){
      if($(a).find('className').text()>$(b).find('className').text()) return 1;
      else if($(a).find('className').text()<$(b).find('className').text()) return -1;
      else return (parseInt($(a).find('number').text())-parseInt($(b).find('number').text()));
    });
    var objectsListSize = objectsList.size();
    //Add the list which propose the user to add an object from a selected class
    var url = wikiViewURL+"WebIDECode/ApplicationObjects?xpage=plain&allClasses=1";
    $.ajax({
      url: url,
      type: "GET",
      contentType: "plain/text",
    }).success(function(dataContent){
      addObjectNode.html(dataContent);
      //loadObjectsDetails : Add details recursively for each object and call the finalizing function
      loadObjectsDetails(objectsList, 0, objectsListSize, '', menuNode, dataNode)
    });
  }


  /**
   * Load details for each object recursively and display them in the page.
   * Call the finilazing function at the end
   * @input List objectsList : the list of objects in the page
   * @input Int index : the object to display in the list
   * @input Int objectsListSize : the objects list size
   * @input String oldClass : the class of the previous object displayed
   * @input DOMElements menuNode, dataNode : The element in which object details have to be added
   */
  function loadObjectsDetails(objectsList, index, objectsListSize, oldClass, menuNode, dataNode, currentClassNode) {
    if(objectsListSize == 0) {endDisplayObjects(objectsListSize);}
    else {
      var className = $(objectsList[index]).find('className').text();
      var number = $(objectsList[index]).find('number').text();
      if(className != oldClass) {
        dataNode.append('<h2 class="wikigeneratedheader editorObjectClassTitle" data-classname="'+className+'">'+className+'</h2>');
        var currentClassNode = $('<div id="editorObjects-'+className+'" class="editorObjectClassDetails"></div>').appendTo(dataNode);
      }
      var url = wikiViewURL+"WebIDECode/ApplicationObjects?xpage=plain&docFullName="+getCurrentSpace(true)+'.'+getCurrentPage(true)+'&className='+className+'&number='+number;
      $.ajax({
        url: url,
        type: "GET",
        contentType: "plain/text",
      }).success(function(dataContent){
        var anchor = $('<div />').append(dataContent).find('h4').attr('id');
        menuNode.append('<a href="#'+anchor+'" class="editorObjectMenuLink" data-classname="'+className+'" data-number="'+number+'">'+className+' - '+number+'</a><br>');
        currentClassNode.append('<div class="editorDisplayedObject">'+dataContent+'</div>');
      }).error(function(xhr) {
        currentClassNode.append('Error while loading object '+className+' '+number+' : '+xhr.statusText+'<br>');
      }).complete(function(xhr) {
        index++;
        if(index < objectsListSize) {loadObjectsDetails(objectsList, index, objectsListSize, className, menuNode, dataNode, currentClassNode);}
        else {endDisplayObjects(objectsListSize);}
      });
    }
  }

  /**
   * Complete the loading of object editor
   * @input number of objects in the page
   */
  function endDisplayObjects(objectsListSize) {
    // Initialize the events (click on link/buttons) for all the objects
    initObjects();
    // Objects should be hidden if there are more than 2
    if(objectsListSize < 3) {$('.editorObjectDetails').show();}
    // Start the code mirror instances for objects with a textarea field
    if(syntaxHighlighting) {initCodeMirrorInternals();}
    reloadHierarchy();
    setSavedContent();
    setLoadingState(false, 'Loaded', 2500);
  }

  // Add the selected object to the page
  function addObject(className) {
    if(!saving && !loading) {
      setSavingState(true);
      setLoadingState(true);
      if(className != "") {
        $.ajax({
          url: wikiViewURL+"WebIDECode/ApplicationObjects?xpage=plain&addObject="+className+"&docFullName="+getCurrentSpace(true)+'.'+getCurrentPage(true),
          type: "GET",
          dataType: "html"
        }).success(function(dataContent) {
          setSavingState(false, 'Saved', 2500);
          var divSelector = 'div[id="editorObjects-'+className+'"]';
          var anchor = $('<div />').append(dataContent).find('h4').attr('id');
          var number = $('<div/>').append(dataContent).find('div[data-number]').attr('data-number');
          // Add the object in the right class if an object of that class is already in that page
          if($(divSelector).length){
            $(divSelector).show();
            $(divSelector).append('<div class="editorDisplayedObject">'+dataContent+'</div>');
          }
          // Else add the object at the end of the page and quick access menu
          else {
            $('#objectIDEform').append('<h2 class="wikigeneratedheader editorObjectClassTitle" data-classname="'+className+'">'+className+'</h2><div id="editorObjects-'+className+'" class="editorObjectClassDetails"><div class="editorDisplayedObject">'+dataContent+'</div></div>');
          }
          // Add the shortcut in the Quick Access Menu
          if($('#editorObjectMenu a[data-classname="'+className+'"]:last').length) {
            $('#editorObjectMenu a[data-classname="'+className+'"]:last').after('<br><a href="#'+anchor+'" class="editorObjectMenuLink" data-classname="'+className+'" data-number="'+number+'">'+className+' - '+number+'</a>');
          }
          else if($('#editorObjectMenu a:last').length) {
            $('#editorObjectMenu a:last').after('<br><a href="#'+anchor+'" class="editorObjectMenuLink" data-classname="'+className+'" data-number="'+number+'">'+className+' - '+number+'</a>');
          }
          else {
            $('#editorObjectMenuContent').append('<a href="#'+anchor+'" class="editorObjectMenuLink" data-classname="'+className+'" data-number="'+number+'">'+className+' - '+number+'</a>');
          }
          initObjects();
          if(syntaxHighlighting) {
            synchronizeObjects();
            $('.CodeMirror').remove();
            $('#objectIDEform textarea').show();
            initCodeMirrorInternals();
          }
        }).complete(function() {
          setLoadingState(false, 'Loaded', 2500);
        });
      }
    }
  }

  // Initialize the different events related to object editor
  function initObjects(){
    $('#objectIDEform textarea').attr('spellcheck','false');
    $('#objectIDEform textarea').attr('autocomplete','off');
    $('#objectIDEform').on('submit', function(e){
      e.preventDefault();
      saveObjects(true, false);
    });
    $('.editorObjectClassTitle').on('click', function() {
      var divSelector = 'div[id="editorObjects-'+$(this).attr('data-classname')+'"]';
      $(divSelector).toggle();
      // If the textarea was initially hidden, Syntax Highlighting editor will only appear when the user click on the textarea
      $(divSelector+' textarea').click();
    });
    $('.editorObjectTitle h4').on('click', function() {
      var divSelector = 'div[id="editorObject-'+$(this).parent().attr('data-classname')+'-'+$(this).parent().attr('data-number')+'"]';
      $(divSelector).toggle();
      // If the textarea was initially hidden, Syntax Highlighting editor will only appear when the user click on the textarea
      $(divSelector+' textarea').click();
    });
    $('.editorObjectMenuLink').on('click', function(e) {
      $('.editorObjectDetails').hide();
      var divSelector = 'div[id="editorObjects-'+$(this).attr('data-classname')+'"]';
      var divSelectorInternal = 'div[id="editorObject-'+$(this).attr('data-classname')+'-'+$(this).attr('data-number')+'"]';
      $(divSelector).show();
      $(divSelectorInternal).show();
      $(divSelector+' textarea').click();
    });
    $('.editorRemoveObject').on('click', function(e) {
      e.preventDefault();
      $('#editorDeletedObjectModalInfo').html($(this).attr('data-classname')+' - '+$(this).attr('data-number'));
      $('#editorDeleteObjectConfirmed').attr('data-classname', $(this).attr('data-classname'));
      $('#editorDeleteObjectConfirmed').attr('data-number', $(this).attr('data-number'));
      if(bootstrap()) {
        $('#editorDeleteObjectModal').modal('show');
      }
      else {
        openModalNoBootstrap('#editorDeleteObjectModal');
      }
    });
    // Event : Expand/Collapse objects list
    $('#editorExpandAllButton').on('click', function(e) {
      e.preventDefault();
      $('.editorObjectDetails, .editorObjectClassDetails').show();
      $('.editorObjectDetails textarea').click();
    });
    $('#editorCollapseAllButton').on('click', function(e) {
      e.preventDefault();
      $('.editorObjectClassDetails').show();
      $('.editorObjectDetails').hide();
    });
    // Event : Add an object
    $('#editorObjectAddButton').on('click', function(e) {
      e.preventDefault();
      var selectedObj = $('#addClassList').val();
      addObject(selectedObj);
    });
  }

  // Remove the specified object from the page
  function removeObject(className, number) {
    if(!saving && !loading) {
      setSavingState(true, 'Deleting object...');
      setLoadingState(true, 'Removing text...');
      var restCurrentSpaces = getRestSpacesString(getCurrentSpace());
      $.ajax({
        url: '/xwiki/rest/wikis/'+getDocumentReferenceData('wiki')+'/'+restCurrentSpaces+'/pages/'+getCurrentPage()+'/objects/'+className+'/'+number,
        type: "DELETE",
      }).success(function(dataContent){
        setSavingState(false, 'Deleted', 2500);
        var divSelectorInternal = 'div[id="editorObject-'+className+'-'+number+'"]';
        $(divSelectorInternal).parent().remove();
        $('#editorObjectMenu a[data-classname="'+className+'"][data-number="'+number+'"]').next('br').remove();
        $('#editorObjectMenu a[data-classname="'+className+'"][data-number="'+number+'"]').remove();
        $('#hierarchySavingState').html('<strong>Deleted</strong>').show().delay(2500).queue(function(n) {
          $(this).hide().html('');
          n();
        });
        if(bootstrap()) {
          $('#editorDeleteObjectModal').modal('hide');
        }
        else {
          closeModalNoBootstrap();
        }
      }).error(function(xhr){
        setSavingState(true, xhr.statusText, 5000, 'red');
      }).complete(function() {
        $('#editorDeletedObjectModalInfo').html('');
        setLoadingState(false, 'Removed', 2500);
      });
    }
  }

  // Save the displayed objects
  function saveObjects(continueEdit, changePage, file, folder) {
    if(!saving) {
      setSavingState(true);
      if(syntaxHighlighting) {
        synchronizeObjects();
      }
      $.ajax({
        url: $('#objectIDEform').attr('action'),
        type: $('#objectIDEform').attr('method'),
        data: $('#objectIDEform').serialize(),
        success: function() {
          // If Save & View button: go to view mode
          if(!continueEdit) {
            window.location.href = wikiViewURL+getCurrentSpace()+'/'+getCurrentPage();
          }
          // Else if Save while going to a new page (button from modal): change the page when the saving is completed
          else if(changePage) {
            if(nextEditor == 'object') {
              goToNewPageObject(file, folder);
            }
            else if (nextEditor == "wiki") {
              goToNewPageWiki(file, folder);
            }
          }
          // Else, Save & Continue eidting the same page: change the value of savedContent
          else {
            setSavedContent();
          }
          setSavingState(false, 'Saved', 2500);
        }
      });
    }
  }

  // Synchronize the original textareas' contents with the codemirrors' contents.
  function synchronizeObjects() {
    codeMirrors.each(function (cMElement) {
      cMElement.save();
    });
  }

  // Get all the inputs (input, textarea, select, etc.) in the object editor, except those for CodeMirror
  function getObjectInputFields() {
    return $('.editorObjectClassDetails textarea, .editorObjectClassDetails input, .editorObjectClassDetails select').not('.CodeMirror textarea')
  }

  //-------------------------------------
  //----------- Hierarchy API -----------
  //-------------------------------------
  //
  //reloadHierarchy()
  //removeFolder(string folder)
  //removePage(string page)
  //addFolder()
  //addPage()
  //addExistingPage()

  function reloadHierarchy() {
    $.ajax({
      url: wikiGetURL+"WebIDECode/ApplicationHierarchy?xpage=plain",
      type: "GET",
      datatype : "html",
      //data : "action="+XWiki.contextaction+"&editor="+currentEditor+"&masterPage="+masterSpace+"."+masterName+"&remotePage="+currentSpaceEscaped+"."+currentPageEscaped+"&hiddenSpaces="+encodeURIComponent(hiddenSpaces)+"&hiddenPages="+encodeURIComponent(hiddenPages),
      data : "action="+XWiki.contextaction+"&editor="+currentEditor+"&masterPage="+masterSpace+"."+masterName+"&remotePage="+getCurrentSpace(true)+"."+getCurrentPage(true)+"&visibleSpaces="+encodeURIComponent(visibleSpaces)+"&visiblePages="+encodeURIComponent(visiblePages),
      contentType: "text/html",
    }).success(function(data){
      //Update the hierarchy content (list of documents only)
      var result = $('<div />').append(data).find('#hierarchyContent').html();
      $('#hierarchyContent').html(result);
      $('#xwikidoctitleinput').val(decodeURIComponent($('#hierarchyDocTitle').val()));
      $('#editorObjectTitle').html(decodeURIComponent($('#hierarchyDocTitle').val()));
      initialization();
    });
  }

  function removeFolder(folder) {
    setHierarchyState('Removing folder...');
    $.ajax({
      url: getDocumentReferenceData('restURL')+"/objects/WebIDECode.ApplicationClass/0/properties/spaces",
      type: "GET",
      contentType: "application/xml",
    }).success(function(res){
      var oldSpaces = $(res).find('value').text();
      var spaceArray = oldSpaces.split('|');
      var indexSpace = $.inArray(decodeURIComponent(folder), spaceArray)
      if(indexSpace >= 0) {
        spaceArray.splice(indexSpace, 1);
        $.ajax({
          url: getDocumentReferenceData('restURL')+"/objects/WebIDECode.ApplicationClass/0/properties/spaces",
          type: "PUT",
          contentType: "application/x-www-form-urlencoded",
          accept: "application/xml",
          data: "property#spaces="+encodeURIComponent(spaceArray),
        }).success(function(){
          setHierarchyState('Folder removed from project', 2500);
          reloadHierarchy();
        }).error(function(xhr){
          setHierarchyState(xhr.statusText, 4000, 'red');
        });
      }
      else {
        console.log("Folder "+folder+" not found in the list of folders in this project.");
      }
    });
  }

  function removePage(page) {
    setHierarchyState('Removing page...');
    $.ajax({
      url: getDocumentReferenceData('restURL')+"/objects/WebIDECode.ApplicationClass/0/properties/pages",
      type: "GET",
      contentType: "application/xml",
    }).success(function(res){
      var oldPages = $(res).find('value').text();
      var pageArray = oldPages.split('|');
      var indexSpace = $.inArray(page, pageArray)
      if(indexSpace >= 0) {
        pageArray.splice(indexSpace, 1);
        $.ajax({
          url: getDocumentReferenceData('restURL')+"/objects/WebIDECode.ApplicationClass/0/properties/pages",
          type: "PUT",
          contentType: "application/x-www-form-urlencoded",
          accept: "application/xml",
          data: "property#pages="+encodeURIComponent(pageArray),
        }).success(function(){
          setHierarchyState('Page removed from project', 2500);
          reloadHierarchy();
        }).error(function(xhr){
          setHierarchyState(xhr.statusText, 4000, 'red');
        });
      }
    });
  }

  function renameHierarchyElement(type, renameFrom, renameTo) {
    if(!saving) {
      setSavingState(true, 'Renaming in progress...');
      $.ajax({
        url: wikiGetURL+"WebIDECode/ApplicationRename",
        type: "POST",
        data: "outputSyntax=plain&"+type+"=1&masterPage="+masterSpace+"."+masterName+"&renameFrom="+renameFrom+"&renameTo="+renameTo,
      }).success(function(data) {
        setSavingState(false, 'Renaming performed', 2500);
        reloadHierarchy();
      }).error(function(xhr) {
        setSavingState(false, xhr.statusText, 4000, 'red');
      });
    }
  }

  /**
   * Add a folder in the hierarchy list :
   * 1/ Check that the folder doesn't already exists
   * 2/ Add the folder in the list alphabetically sorted
   * 3/ Update the hierarchy
   * @input String folder
  */
  function addFolder(folder) {
    setHierarchyState('Adding folder...');
    $.ajax({
      url: getDocumentReferenceData('restURL')+"/objects/WebIDECode.ApplicationClass/0/properties/spaces",
      type: "GET",
      contentType: "application/xml",
    }).success(function(res){
      var oldSpaces = $(res).find('value').text();
      var spaceArray = oldSpaces.split('|');
      if($.inArray(folder, spaceArray) == -1) {
        spaceArray.push(folder);
        spaceArray = spaceArray.sort();
        $.ajax({
          url: getDocumentReferenceData('restURL')+"/objects/WebIDECode.ApplicationClass/0/properties/spaces",
          type: "PUT",
          contentType: "application/x-www-form-urlencoded",
          accept: "application/xml",
          data: "property#spaces="+encodeURIComponent(spaceArray),
        }).success(function(){
          $('#hierarchyNewFolderName').val('');
          if(bootstrap()) {
            $('#hierarchyFolderModal').modal('hide');
          }
          else {
            closeModalNoBootstrap();
          }
          setHierarchyState('Folder added', 2500);
          reloadHierarchy();
        }).error(function(xhr){
          $('#hierarchyNewFolderName').val('');
          if(bootstrap()) {
            $('#hierarchyFolderModal').modal('hide');
          }
          else {
            closeModalNoBootstrap();
          }
          setHierarchyState(xhr.statusText, 4000, 'red');
        });
      }
    });
  }

  /**
   * Add a page in a folder :
   * 1/ Check that the page doesn't already exists
   * 2/ Create the page in the wiki
   * 3/ Update the hierarchy
   * @input String folder, String file
  */
  function addPage(folder, file) {
    var restSpaces = getRestSpacesString(folder);
    setHierarchyState('Adding page...');
    $.ajax({
      url: "/xwiki/rest/wikis/"+getDocumentReferenceData('wiki')+"/"+restSpaces+"/pages/"+file,
      type: "GET",
      contentType: "application/xml",
    }).success(function(xhr){
      $('#hierarchyExistingPageModalError').html('ERROR : A page with the same name already exist').show().delay(4000).queue(function(n) {
        $(this).hide().html('');
        n();
      });
    }).error(function(res){
      $.ajax({
        url: "/xwiki/rest/wikis/"+getDocumentReferenceData('wiki')+"/"+restSpaces+"/pages/"+file,
        type: "PUT",
        contentType: "application/x-www-form-urlencoded",
        accept: "application/xml",
        data: "title="+file+"&parent="+currentSpaceEscaped+"."+currentPageEscaped,
      }).success(function(){
        $('#hierarchyNewPageName').val('');
        if(bootstrap()) {
          $('#hierarchyPageModal').modal('hide');
        }
        else {
          closeModalNoBootstrap();
        }
        setHierarchyState('Page added', 2500);
        reloadHierarchy();
      }).error(function(xhr){
        $('#hierarchyNewPageName').val('');
        if(bootstrap()) {
          $('#hierarchyPageModal').modal('hide');
        }
        else {
          closeModalNoBootstrap();
        }
        setHierarchyState(xhr.statusText, 4000, 'red');
      });
    });
  }

  /**
   * Add an existing page in the hierarchy list :
   * 1/ Check that the page exists
   * 2/ Add the page in the list alphabetically sorted
   * 3/ Update the hierarchy
   * @input String fullName
  */
  function addExistingPage(pageFullName) {
    var existingPage = pageFullName;
    var existingPageList = getSpaceAndName(existingPage);
    var existingPageSpace = existingPageList[0];
    var existingPageName = existingPageList[1];
    var restSpaces = getRestSpacesString(existingPageSpace);
    var restSpacesMaster = getRestSpacesString(masterSpace);
    setHierarchyState('Adding page...');
    $.ajax({
      url: "/xwiki/rest/wikis/"+getDocumentReferenceData('wiki')+"/"+restSpaces+"/pages/"+existingPageName,
      type: "GET",
      contentType: "application/xml",
    }).error(function(){
      $('#hierarchyExistingPageModalError').html('ERROR : Unexisting page').show().delay(4000).queue(function(n) {
        $(this).hide().html('');
        n();
      });
    }).success(function(){
      $.ajax({
        url: "/xwiki/rest/wikis/"+getDocumentReferenceData('wiki')+"/"+restSpacesMaster+"/pages/"+masterName+"/objects/WebIDECode.ApplicationClass/0/properties/pages",
        type: "GET",
        contentType: "application/xml",
      }).success(function(res){
        var oldPages = $(res).find('value').text();
        var pageArray = oldPages.split('|');
        if($.inArray(existingPage, pageArray) == -1) {
          pageArray.push(existingPage);
          pageArray = pageArray.sort();
          $.ajax({
            url: getDocumentReferenceData('restURL')+"/objects/WebIDECode.ApplicationClass/0/properties/pages",
            //url: "/xwiki/rest/wikis/"+getDocumentReferenceData('wiki')+"/"+restSpacesMaster+"/pages/"+masterName+"/objects/WebIDECode.ApplicationClass/0/properties/pages",
            type: "PUT",
            contentType: "application/x-www-form-urlencoded",
            accept: "application/xml",
            data: "property#pages="+encodeURIComponent(pageArray),
          }).success(function(){
            $('#hierarchyExistingPageName').val('');
            if(bootstrap()) {
              $('#hierarchyExistingPageModal').modal('hide');
            }
            else {
              closeModalNoBootstrap();
            }
            setHierarchyState('Page added', 2500);
            reloadHierarchy();
          }).error(function(xhr){
            $('#hierarchyExistingPageName').val('');
            if(bootstrap()) {
              $('#hierarchyExistingPageModal').modal('hide');
            }
            else {
              closeModalNoBootstrap();
            }
            setHierarchyState(xhr.statusText, 4000, 'red');
          });
        }
      });
    });
  }

  // Initialize the WebIDE wiki editor
  function initWikiEditor(file, folder) {
    mainBlock = '#contentMainBlock';
    buttonBarBlock = '#editorWikiButtonBar';
    $(mainBlockContainer).css({"height" : "650px"});
    XWiki.editor = 'wiki';
    // The textarea may have been hidden by Syntax Highlighting before
    $('#contentMainBlock').html('<textarea spellcheck="false" autocomplete="off" id="content"></textarea>');
    $('.editorEditElement').css({"display" : "block"});
    $('.editorWikiElement').css({"display" : "block"});
    $('#xwikidoctitleinput').css({"display" : "inline-block"});
    $('#editorObjectTitle').hide();
    if(syntaxHighlighting) {
      var insertTags = function(){return true};
      // Add the CodeMirror editor
      initCodeMirrorInternals();
    }
    else {
      loadFirstPageWiki(file, folder);
    }
  }
  // Initialize the hierarchy in the old XWiki class editor
  function initClassEditor() {
    currentEditor = "class";
    mainBlock = '#propupdate';
    mainBlockContainer = '#mainEditArea';
    initialization();
  }
  // Initialize the WebIDE object editor
  function initObjectEditor(file, folder) {
    mainBlock = '#objectMainBlock';
    $(mainBlockContainer).css({"height" : "auto"});
    $('.editorObjectElement').css({"display" : "block"});
    $('.editorEditElement').css({"display" : "block"});
    $('#xwikidoctitleinput').hide();
    $('#editorObjectTitle').show();
    addButtonEvents('object');
    XWiki.editor = 'object';
    loadObject(file, folder);
  }
  // Initialize the hierarchy in Master page view mode
  function initViewEditor() {
    mainBlock = '#displayBlock';
    $('.editorViewElement').css({"display" : "block"});
    $('#xwikidoctitleinput').hide();
    $('#editorObjectTitle').show();
    initialization();
  }

  // Hide the old editor when switching between object and wiki
  function changeEditor(oldEditor) {
    if(oldEditor == 'wiki') {
      setStarEvent(false);
      $('#contentMainBlock').html('');
    }
    else if(oldEditor == 'object') {
      $('#objectMainBlock').html('');
    }
    $('.CodeMirror').remove();
    codeMirrors = [];
  }

  function goToNewPageWiki(file, folder) {
    if(currentEditor != 'wiki') {
      changeEditor(currentEditor);
      currentEditor = "wiki";
      $('.editorElement').hide();
      initWikiEditor(file, folder);
    }
    else {
      loadPage(file, folder);
    }
  }
  function goToNewPageObject(file, folder) {
    if(currentEditor != 'object') {
      changeEditor(currentEditor);
      currentEditor = "object";
      $('.editorElement').hide();
      initObjectEditor(file, folder);
    }
    else {
      loadObject(file, folder);
    }
  }
  /**
   * Load the first page after wiki editor is initialized
   * Register new actions for saving buttons and new events for wiki editor
   */

  function loadFirstPageWiki(file, folder) {
    setStarEvent(true);
    addButtonEvents('wiki');
    loadPage(file, folder);
  }

  /**
  ** Manage the saving events
  ** 1/ Star which appears when the current edited page is unsaved
  ** 2/ Saving events when the user clicks on the saving buttons or uses the shortcuts
  */
  function setStarEvent(state) {
    var elementEditor = $(textEditor);
    // Add a star in the hierarchy if the content has changed
    if(state == true) {
      elementEditor.on('keyup', addStarEvent);
    }
    else {
      elementEditor.off('keyup', addStarEvent);
    }
  }
  function addStarEvent() {
    if(syntaxHighlighting) {
      $('#hierarchyStar').show();
    }
    else {
      if(savedContent != getCurrentContent()) {
        $('#hierarchyStar').show();
      }
      else {
        $('#hierarchyStar').hide();
      }
    }
  }
  function addButtonEvents(editor) {
    shortcut.remove("Alt+Shift+S");
    shortcut.remove("Alt+S");
    shortcut.remove("Alt+C");
    if(editor == 'wiki') {
      // Add events on buttons and shortcuts
      $('#hierarchySaveButtonIDE').click(function(e){
        e.preventDefault();
        savePage(currentSpace, currentPage, getCurrentContent(), $('#xwikidoctitleinput').val(), true, true);
      });
      $('#hierarchySaveViewButtonIDE').click(function(e){
        e.preventDefault();
        savePage(currentSpace, currentPage, getCurrentContent(), $('#xwikidoctitleinput').val(), true, false);
      });
      $('#hierarchyDiscardViewButtonIDE').click(function(e){
        e.preventDefault();
        cancelAndViewIDE();
      });
      shortcut.add("Alt+Shift+S", function() {
        if(syntaxHighlighting) {
          synchronizeObjects();
        }
        savePage(currentSpace, currentPage, getCurrentContent(), $('#xwikidoctitleinput').val(), true, true);
      });
      shortcut.add("Alt+S", function() {
        if(syntaxHighlighting) {
          synchronizeObjects();
        }
        savePage(currentSpace, currentPage, getCurrentContent(), $('#xwikidoctitleinput').val(), true, false);
      });
      shortcut.add("Alt+C", function() {cancelAndViewIDE();});
    }
    else if(editor == 'object') {
      // Add events on buttons and shortcuts
      $('#hierarchySaveButtonIDE').click(function(e){
        e.preventDefault();
        saveObjects(true, false);
      });
      $('#hierarchySaveViewButtonIDE').click(function(e){
        e.preventDefault();
        saveObjects(false, false);
      });
      $('#hierarchyDiscardViewButtonIDE').click(function(e){
        e.preventDefault();
        cancelAndViewIDE();
      });
      $('#objectIDEform')
      shortcut.add("Alt+Shift+S", function() {
        saveObjects(true, false);
      });
      shortcut.add("Alt+S", function() {
        saveObjects(false, false);
      });
      shortcut.add("Alt+C", function() {cancelAndViewIDE();});
    }
  }

  // Read and write cookies with specified expiration date
  function setCookieHierarchy(cname, cvalue, exdays) {
    var d = new Date();
    d.setTime(d.getTime() + (exdays*24*60*60*1000));
    var expires = "expires="+d.toUTCString();
    document.cookie = masterName+"-"+cname + "=" + cvalue + "; " + expires + ";path=/";
  }
  function getCookieHierarchy(cname) {
    var name = masterName+"-"+cname + "=";
    var ca = document.cookie.split(';');
    for(var i=0; i<ca.length; i++) {
      var c = ca[i];
      while (c.charAt(0)==' ') c = c.substring(1);
      if (c.indexOf(name) == 0) return c.substring(name.length,c.length);
    }
    return "";
  }

  /** NEW
   * Nested Spaces Update
   * - Check XWiki version (nested spaces if >= 7.2)
   * - Transform nested space to rest format (spaces/Space1/spaces/Space2)
   */

  /**
   * Check if Nested spaces are available if that version of XWiki
   * @return boolean
   */
  function checkNestedSpaces() {
    var xwikiVersion = $('#xwikiVersion').val();
    var baseVersion = xwikiVersion.match(/([0-9.]+).*/);
    return (parseFloat(baseVersion)>=7.2);
  }

  /**
   * @param spaces The string describing spaces with XWiki classic format 'A.B'
   * @return The string describing spaces with XWiki REST format 'spaces/A/spaces/B'
   */
  function getRestSpacesString(spaces) {
    if(NESTED_SPACES) {
      var spacesEnc = spaces.replace(/%5C\./g, '%5C%2E').replace(/\\\./g, '%5C%2E');
      var spacesList = spacesEnc.split('.');
      var restFormat = '';
      for(var i=0; i<spacesList.length; i++) {
        restFormat += 'spaces/'+spacesList[i].replace('%5C%2E', '\.')+'/';
      }
      return restFormat.substring(0, restFormat.length - 1);
    }
    else return 'spaces/'+spaces+'';
  }

  /**
   * @param spaces The string describing spaces with XWiki classic format 'A.B'
   * @return The string describing spaces with XWiki URL format 'A/B'
   */
  function getURLSpacesString(spaces) {
    if(NESTED_SPACES) {
      var spacesEsc = spaces.replace(/%5C\./g, '%5C%2E').replace(/\\\./g, '%5C%2E');
      var spacesList = spacesEsc.split('.');
      var restFormat = '';
      for(var i=0; i<spacesList.length; i++) {
        restFormat += spacesList[i].replace('%5C%2E', '\.')+'/';
      }
      return restFormat.substring(0, restFormat.length - 1);
    }
    else return spaces;
  }

  /**
   * @param spaces The string describing a space with XWiki classic format 'A.B' (without nested space)
   * @return The string describing escaped space name 'A\.B'
   */
  function getSpacesEscaped(spaces) {
    if(NESTED_SPACES) {
      return spaces;
    }
    else return spaces.replace('%5C', '%5C%5C').replace(/\\/g, '%5C%5C').replace(/\./g, '%5C.');
  }

  /**
   * @param fullName The string describing the full name of a page 'A.B.WebHome'
   * @return The list of the space and the name ['A.B', 'WebHome']
   */
  function getSpaceAndName(fullName) {
    var fullNameEsc = fullName.replace(/%5C\./g, '%5C%2E').replace(/\\\./g, '%5C%2E');
    var existingPageSpace = fullNameEsc.substring(0, fullNameEsc.lastIndexOf(".")).replace('%5C%2E', '\.') || '';
    var existingPageName = fullNameEsc.substring(fullNameEsc.lastIndexOf(".") + 1, fullNameEsc.length).replace('%5C%2E', '\.') || '';
    return [existingPageSpace, existingPageName];
  }

});
});

