    // User wants to edit (wiki) a new page
    $('.editPage').click(function(e){
      if(!saving && !loading) {
        e.preventDefault();
        $('#hierarchyNextDocName').val($(this).attr('data-page'));
        $('#hierarchyNextDocSpace').val($(this).attr('data-space'));

        if(syntaxHighlighting) {
