        var currentContent = '';
        if(currentEditor == 'object') {
          var arrayVal = getObjectInputFields();
          arrayVal.each(function(elemt, index) {
            currentContent += $(index).val();
          });
        }
        else {
          currentContent = $(textEditor).val();
