property.code:
require(['jquery'], function ($) {
$(function(){
  var currentPage = encodeURIComponent(XWiki.currentPage);
  var currentSpace = encodeURIComponent(XWiki.currentSpace);
  var currentPageEscaped = encodeURIComponent(XWiki.currentPage.replace(/\\/g, '\\\\').replace(/\./g, '\\\.'));
  var currentSpaceEscaped = encodeURIComponent(XWiki.currentSpace.replace(/\\/g, '\\\\').replace(/\./g, '\\\.'));
  var wikiViewURL = '/xwiki/bin/view/';
  if(window.docviewurl.indexOf('/'+XWiki.currentSpace+'/') >= 0) {
    wikiViewURL = window.docviewurl.substring(0,window.docviewurl.indexOf('/'+XWiki.currentSpace+'/')+1);
  }

  // Determine if that page is part of a project. Add the XWebIDE link in the Actions menu if that's the case and the "W" shortcut to use the XWebIDE editor.
  $.ajax({
    url: wikiViewURL+"WebIDECode/ApplicationCheck?xpage=plain&outputSyntax=plain&doc="+currentSpaceEscaped+"."+currentPageEscaped,
    type: "GET",
    contentType: "application/json",
  }).success(function(data){
    if(data.error == "-1") {
      if(XWiki.contextaction == 'edit' || XWiki.contextaction == 'view') {
        if($('#tmEdit .submenu .submenuseparator').length) { //Colibri Skin
          $('#tmEdit .submenu .submenuseparator').before('<span class="submenuitem "><a href="/xwiki/bin/edit/'+data.documentSpace+'/'+data.documentName+'?language=default&editor=wiki#!XWebIDE" id="tmEditWebIDE" class="tmEditXWebIDE" title="Edit '+data.documentName+' in XWebIDE as part of '+data.project+'">XWebIDE</a></span>');
          $('.tmEditXWebIDE').css({
            "backgroundImage": "url('/xwiki/resources/icons/silk/folder_page.png')"
          });
        }
        else { //Flamingo
          $('#tmEdit .dropdown-menu .divider').before('<li class=""><a href="/xwiki/bin/edit/'+data.documentSpace+'/'+data.documentName+'?language=default&editor=wiki#!XWebIDE" id="tmEditWebIDE" title="Edit '+data.documentName+' in XWebIDE as part of '+data.project+'"><span class="glyphicon  glyphicon-folder-open"></span> XWebIDE</a></li>');
        }
      }
      if(XWiki.contextaction == 'edit' && window.location.hash && !hierarchyStatus) {
        if(window.location.hash.substring(1) == '!XWebIDE') {
          displayHierarchy(data.masterSpace, data.masterName);
        }
      }
      else if(XWiki.contextaction == 'edit' && !hierarchyStatus) {
        $('#tmEditWebIDE').click(function(){
          if(!hierarchyStatus) {
            displayHierarchy(data.masterSpace, data.masterName);
          }
        });
      }
      shortcut.add("W", function() { window.location.href = '/xwiki/bin/edit/'+encodeURIComponent(unescape(data.documentSpace))+'/'+encodeURIComponent(unescape(data.documentName))+'?language=default&editor=wiki#!XWebIDE'; },{'disable_in_input':'true'});
    }

    if(data.sheetError == "-1") {
      if(XWiki.contextaction == 'edit' || XWiki.contextaction == 'view') {
        if($('#tmEdit .submenu .submenuseparator').length) { //Colibri Skin
          $('#tmEdit .submenu .submenuseparator').before('<span class="submenuitem "><a href="/xwiki/bin/edit/'+data.sheetDocumentSpace+'/'+data.sheetDocumentName+'?language=default&editor=wiki#!XWebIDE" id="tmEditWebIDE" class="tmEditXWebIDESheet" title="Edit sheet '+data.sheetDocumentSpace+'.'+data.sheetDocumentName+' in XWebIDE as part of '+data.sheetProject+'">XWebIDE (Sheet)</a></span>');
          $('.tmEditXWebIDESheet').css({
            "backgroundImage": "url('/xwiki/resources/icons/silk/folder_page.png')"
          });
        }
        else { //Flamingo
          $('#tmEdit .dropdown-menu .divider').before('<li class=""><a href="/xwiki/bin/edit/'+data.sheetDocumentSpace+'/'+data.sheetDocumentName+'?language=default&editor=wiki#!XWebIDE" id="tmEditWebIDE" title="Edit sheet '+data.sheetDocumentSpace+'.'+data.sheetDocumentName+' in XWebIDE as part of '+data.sheetProject+'"><span class="glyphicon  glyphicon-folder-open"></span> XWebIDE (Sheet)</a></li>');
        }
      }
      shortcut.add("Shift+W", function() { window.location.href = '/xwiki/bin/edit/'+encodeURI(data.sheetDocumentSpace)+'/'+encodeURI(data.sheetDocumentName)+'?language=default&editor=wiki#!XWebIDE'; },{'disable_in_input':'true'});
    }

  });


  var hierarchyStatus = false;
  function displayHierarchy(masterSpace, masterName) {
    if(!hierarchyStatus && !document.getElementById('hierarchyBlock')) {
      hierarchyStatus = true;
      var syntaxHighlighting = ($('.CodeMirror').length>0 || typeof(CodeMirror) !== 'undefined');
      var hiddenSpaces = getCookieHierarchy(escape(masterName), 'hiddenSpaces');
      var hiddenPages = getCookieHierarchy(escape(masterName), 'hiddenPages');
      var wikiGetURL = '/xwiki/bin/get/';
      if(window.docgeturl.indexOf('/get/') != 0) wikiGetURL = window.docgeturl.substring(0,window.docgeturl.indexOf('/get/')+5);
      $.ajax({
        url: wikiGetURL+"WebIDECode/ApplicationHierarchy?xpage=plain&action="+XWiki.contextaction+"&editor="+XWiki.editor+"&masterPage="+encodeURI(masterSpace)+"."+encodeURI(masterName)+"&remotePage="+currentSpaceEscaped+"."+currentPageEscaped+"&hiddenSpaces="+encodeURIComponent(hiddenSpaces)+"&hiddenPages="+encodeURIComponent(hiddenPages),
        type: "GET",
        contentType: "text/plain",
      }).success(function(data){
        var mainBlock = '';
        if(XWiki.editor == "wiki") {
          if(syntaxHighlighting) {
            mainBlock = '.CodeMirror';
            codeMirrors[0].setOption('lineWrapping', true)
          }
          else {
            mainBlock = '#content';
          }
        }
        else if(XWiki.editor == "object"){
          mainBlock = '#update';
        }
        else if(XWiki.editor == "class"){
          mainBlock = '#propupdate';
        }
        $(mainBlock).before('<div id="hierarchyBlock" >'+data+'</div>');
        $('head').append('<link rel="stylesheet" type="text/css" href="/xwiki/bin/ssx/WebIDECode/ApplicationHierarchy"/>');
        $('head').append('<script type="text/javascript" src="/xwiki/bin/jsx/WebIDECode/ApplicationHierarchy" defer="defer"></script>');
        $(mainBlock).css({
          "width" : "83%",
          "maxWidth" : "83%",
          "resize" : "none"
        });
        $("#hierarchyBlock").css({
          "width" : "17%",
          "maxWidth" : "17%",
          "resize" : "none",
        });
        if(XWiki.editor == "wiki") {
          $('#editPanels').remove();
          $('.leftsidecolumns.main').css({'width' : '100%'});
          $(mainBlock).css({
            "height": "95%"
          });
          $("#hierarchyBlock").css({
            "height": "95%",
            "backgroundColor" : $('div.leftmenu2').css('backgroundColor')
          });
          $(".hierarchy-remove").css({
            "backgroundColor" : $('div.leftmenu2').css('backgroundColor')
          });
          $('#content').attr('autocomplete','off');
          $('#content').attr('spellcheck','false');
          $("#xwikieditcontentinner").css({
            "height" : "600px",
            "overflow" : "hidden",
            "padding" : "0",
            "resize" : "vertical",
            "minHeight" : "600px"
          });
          $('.alleditcontent').css({
            "margin": "0px 25px 25px 20px",
          });
          $('.bottombuttons').html('<div id="hierarchyBottomActionsBar"><input type="button" id="hierarchySaveButtonIDE" class="btn btn-primary" value="Save & Continue" title="Save the page and continue editing" /> <input type="button" id="hierarchySaveViewButtonIDE" class="btn btn-success" value="Save & View" title="Save the page and view it" /> <input type="button" id="hierarchyDiscardViewButtonIDE" class="btn btn-default" value="Discard & View" title="View the page without saving" /> <span id="hierarchySavingState"></span><span id="hierarchyLoadingState"></span></div>');
        }
        else if(XWiki.editor == "object" || XWiki.editor == "class"){
          $("#hierarchyBlock").css({
            "display" : "inline-block",
            "maxHeight" : ($(window).height())+"px"
          });
          $(mainBlock).css({
            "display" : "inline-block",
            "float" : "right",
            "minHeight" : ($("#hierarchyBlock").height())+"px"
          });
          $("#xwikieditcontent").css({
            "padding" : "10px",
            "resize" : "vertical"
          });
          $('.bottombuttons').css({
            "paddingLeft" : "20px",
          });
          $('#xwikiobjects textarea').attr('spellcheck','false');
          // Fix the position of the hierarchy when the page contains lots of objects
          $(window).resize(function() {$("#hierarchyBlock").css({"maxHeight" : ($(window).height())+"px"});});
          var hierarchyTop = $('#hierarchyBlock').offset().top; // returns number
          var hierarchyLeft = $('#hierarchyBlock').offset().left; // returns number
          $(window).scroll(function(){ // scroll event
          var footerTop = $('#footerglobal').offset().top; // returns number 
            var windowTop = $(window).scrollTop(); // returns number 
            if (hierarchyTop < windowTop && (windowTop+$('#hierarchyBlock').height()) < footerTop){
              $('#hierarchyBlock').css({ position: 'fixed', top : 0, left : hierarchyLeft, width: '17%'});
            }
            else if(hierarchyTop < windowTop && (windowTop+$('#hierarchyBlock').height()) > footerTop) {
              $('#hierarchyBlock').css({ position: 'fixed', top: -(windowTop +$(window).height()-footerTop), left : hierarchyLeft, width: '17%'});
            }
            else {
              $('#hierarchyBlock').css('position','static');
            }
          });
        }
      });
    }
  }

  function getCookieHierarchy(masterName, cname) {
    var name = masterName+"-"+cname + "=";
    var ca = document.cookie.split(';');
    for(var i=0; i<ca.length; i++) {
      var c = ca[i];
      while (c.charAt(0)==' ') c = c.substring(1);
      if (c.indexOf(name) == 0) return c.substring(name.length,c.length);
    }
    return "";
  }

});
});
